import React, { useState } from "react";
import { TextField, Card, CardContent, List, ListItem, Button } from "@mui/material";
import { motion } from "framer-motion";

const SalesRegister = () => {
  const [search, setSearch] = useState("");
  const products = ["Product A", "Product B", "Product C", "Product D"]; // Mock product list
  const recentSales = ["Product X", "Product Y", "Product Z"]; // Mock recent sales

  // Define a set of colors
  const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#33FFF5", "#F5B041"];

  return (
    <Card component={motion.div} whileHover={{ scale: 1.02 }} sx={{ p: 2, maxWidth: 400 }}>
      <CardContent>
        <TextField
          label="Search Product"
          variant="outlined"
          fullWidth
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        {/* Product Suggestions with Dynamic Colors */}
        {search && (
          <List>
            {products
              .filter(p => p.toLowerCase().includes(search.toLowerCase()))
              .map((p, index) => (
                <ListItem
                  key={index}
                  button
                  sx={{ bgcolor: colors[index % colors.length], color: "#fff", borderRadius: 2, my: 1 }}
                >
                  {p}
                </ListItem>
              ))}
          </List>
        )}

        {/* Recent & Frequent Sales */}
        <List sx={{ mt: 2, bgcolor: "#f5f5f5", borderRadius: 2, p: 1 }}>
          <strong>Frequent & Recent Sales</strong>
          {recentSales.map((p, index) => (
            <ListItem key={index} button sx={{ bgcolor: colors[index % colors.length], color: "#fff", borderRadius: 2, my: 1 }}>
              {p}
            </ListItem>
          ))}
        </List>

        {/* Register Sale Button */}
        <Button variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
          Register Sale
        </Button>
      </CardContent>
    </Card>
  );
};

export default SalesRegister;
