import React, { useState } from "react";
import { TextField, Card, CardContent, List, ListItem, Button, Box } from "@mui/material";
import { motion } from "framer-motion";

const SalesRegister = () => {
  const [search, setSearch] = useState("");
  const products = ["Product A", "Product B", "Product C", "Product D", "Product E", "Product F"];
  const recentSales = ["Product X", "Product Y", "Product Z"];

  // Define a set of colors for styling
  const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#33FFF5", "#F5B041"];

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      sx={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        p: 3,
        bgcolor: "#f5f5f5"
      }}
    >
      <Card sx={{ width: "80%", p: 2, borderRadius: 2 }}>
        <CardContent>
          {/* Search Bar */}
          <TextField
            label="Search Product"
            variant="outlined"
            fullWidth
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          {/* Product Suggestions (Displayed in a row like an array list) */}
          {search && (
            <Box
              sx={{
                display: "flex",
                overflowX: "auto",
                gap: 1,
                mt: 2,
                p: 1,
                bgcolor: "white",
                borderRadius: 2,
                boxShadow: 2
              }}
            >
              {products
                .filter(p => p.toLowerCase().includes(search.toLowerCase()))
                .map((p, index) => (
                  <Box
                    key={index}
                    sx={{
                      bgcolor: colors[index % colors.length],
                      color: "white",
                      px: 2,
                      py: 1,
                      borderRadius: 2,
                      whiteSpace: "nowrap",
                      cursor: "pointer",
                      "&:hover": { opacity: 0.8 }
                    }}
                  >
                    {p}
                  </Box>
                ))}
            </Box>
          )}

          {/* Recent & Frequent Sales (Also in a horizontal list) */}
          <Box
            sx={{
              display: "flex",
              overflowX: "auto",
              gap: 1,
              mt: 2,
              p: 1,
              bgcolor: "#fff",
              borderRadius: 2,
              boxShadow: 2
            }}
          >
            <strong>Frequent & Recent Sales:</strong>
            {recentSales.map((p, index) => (
              <Box
                key={index}
                sx={{
                  bgcolor: colors[index % colors.length],
                  color: "white",
                  px: 2,
                  py: 1,
                  borderRadius: 2,
                  whiteSpace: "nowrap"
                }}
              >
                {p}
              </Box>
            ))}
          </Box>

          {/* Register Sale Button */}
          <Button variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            Register Sale
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default SalesRegister;
