import React, { useState } from "react";
import { TextField, Card, CardContent, List, ListItem, Button, Box } from "@mui/material";
import { motion } from "framer-motion";

const SalesRegister = () => {
  const [search, setSearch] = useState("");
  const products = ["Product A", "Product B", "Product C", "Product D"]; // Mock product list
  const recentSales = ["Product X", "Product Y", "Product Z"]; // Mock recent sales

  // Define a set of colors
  const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#33FFF5", "#F5B041"];

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      sx={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        p: 3,
        bgcolor: "#f5f5f5"
      }}
    >
      <Card sx={{ width: "60%", p: 2, borderRadius: 2 }}>
        <CardContent>
          {/* Search Bar */}
          <TextField
            label="Search Product"
            variant="outlined"
            fullWidth
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          {/* Product Suggestions (Full-width dropdown) */}
          {search && (
            <List sx={{ width: "100%", mt: 1, bgcolor: "white", borderRadius: 2, boxShadow: 2 }}>
              {products
                .filter(p => p.toLowerCase().includes(search.toLowerCase()))
                .map((p, index) => (
                  <ListItem
                    key={index}
                    button
                    sx={{
                      bgcolor: colors[index % colors.length],
                      color: "#fff",
                      borderRadius: 0,
                      textAlign: "center",
                      "&:hover": { opacity: 0.8 }
                    }}
                  >
                    {p}
                  </ListItem>
                ))}
            </List>
          )}

          {/* Recent & Frequent Sales */}
          <List sx={{ mt: 2, bgcolor: "#fff", borderRadius: 2, p: 1, boxShadow: 2 }}>
            <strong>Frequent & Recent Sales</strong>
            {recentSales.map((p, index) => (
              <ListItem key={index} button sx={{ bgcolor: colors[index % colors.length], color: "#fff", borderRadius: 0 }}>
                {p}
              </ListItem>
            ))}
          </List>

          {/* Register Sale Button */}
          <Button variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            Register Sale
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default SalesRegister;
