import React, { useState, useEffect } from "react";
import { TextField, Card, CardContent, List, ListItem, Button, Box, Typography, IconButton } from "@mui/material";
import { motion } from "framer-motion";

const SalesRegister = () => {
  const [search, setSearch] = useState("");
  const [shoppingList, setShoppingList] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/shop/stock")
      .then(response => response.json())
      .then(data => setProducts(data.stock));
  }, []);

  useEffect(() => {
    setTotalAmount(shoppingList.reduce((acc, item) => acc + item.price * item.quantity, 0));
  }, [shoppingList]);

  const addToShoppingList = (product) => {
    setShoppingList((prevList) => {
      const existingItem = prevList.find(item => item.name === product.name);
      if (existingItem) {
        return prevList;
      }
      return [...prevList, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (product, quantity) => {
    setShoppingList((prevList) =>
      prevList.map(item => item.name === product.name ? { ...item, quantity: quantity } : item)
    );
  };

  const removeFromShoppingList = (product) => {
    setShoppingList((prevList) => prevList.filter(item => item.name !== product.name));
  };

  const processSale = () => {
    console.log("Processing sale:", shoppingList);
    fetch("http://localhost:8000/shop/sales", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ sales: shoppingList })
    }).then(() => setShoppingList([]));
  };

  return (
    <Box component={motion.div} initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}
      sx={{ width: "100vw", height: "100vh", display: "flex", alignItems: "center", justifyContent: "center", p: 3, bgcolor: "#f5f5f5" }}>

      {/* Shopping List */}
      <Box sx={{ width: "30%", bgcolor: "white", p: 2, borderRadius: 2, boxShadow: 2 }}>
        <Typography variant="h6">Shopping List</Typography>
        <List>
          {shoppingList.map((item, index) => (
            <ListItem key={index} sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
              {item.name} - {item.unit} - Ksh {item.price * item.quantity}
              <TextField
                type="number"
                size="small"
                sx={{ width: "60px", ml: 2 }}
                value={item.quantity}
                onChange={(e) => updateQuantity(item, parseFloat(e.target.value) || 0)}
              />
              <Button size="small" color="error" onClick={() => removeFromShoppingList(item)}>Remove</Button>
            </ListItem>
          ))}
        </List>
        <Typography variant="h6">Total: Ksh {totalAmount}</Typography>
      </Box>

      {/* Main Sale Registration Panel */}
      <Card sx={{ width: "50%", p: 2, borderRadius: 2, ml: 2 }}>
        <CardContent>
          <TextField label="Search Product" variant="outlined" fullWidth value={search} onChange={(e) => setSearch(e.target.value)} />

          {/* Product Suggestions */}
          {search && (
            <Box sx={{ display: "flex", overflowX: "auto", gap: 1, mt: 2, p: 1, bgcolor: "white", borderRadius: 2, boxShadow: 2 }}>
              {products.filter(p => p.name.toLowerCase().includes(search.toLowerCase())).map((p, index) => (
                <Box key={index} sx={{ bgcolor: "#007BFF", color: "white", px: 2, py: 1, borderRadius: 2, whiteSpace: "nowrap", cursor: "pointer", "&:hover": { opacity: 0.8 } }} onClick={() => addToShoppingList(p)}>
                  {p.name} - {p.unit} - Ksh {p.price}
                </Box>
              ))}
            </Box>
          )}

          {/* Register Sale Button */}
          <Button variant="contained" color="primary" fullWidth sx={{ mt: 2 }} onClick={processSale}>Register Sale</Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default SalesRegister;
