import React, { useState, useEffect } from "react";
import { TextField, Card, CardContent, Box, Button, Typography } from "@mui/material";
import { motion } from "framer-motion";

const SalesRegister = () => {
  const [search, setSearch] = useState("");
  const [products, setProducts] = useState([]);
  const [recentSales, setRecentSales] = useState([]);
  const [lowStock, setLowStock] = useState([]);

  // Define a set of colors for styling
  const colors = ["#FF5733", "#33FF57", "#3357FF", "#FF33A8", "#33FFF5", "#F5B041"];

  useEffect(() => {
    fetchProducts();
    fetchLowStock();
  }, []);

  // Fetch stock products
  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:8000/shop/stock");
      const data = await res.json();
      setProducts(data.stock || []);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  // Fetch low-stock alerts
  const fetchLowStock = async () => {
    try {
      const res = await fetch("http://localhost:8000/shop/stock/low");
      const data = await res.json();
      setLowStock(data.low_stock_items || []);
    } catch (error) {
      console.error("Error fetching low stock:", error);
    }
  };

  // Handle sale registration
  const handleSale = async (productName) => {
    try {
      const res = await fetch(`http://localhost:8000/shop/stock/update?name=${productName}&quantity_sold=1`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
      });

      const data = await res.json();
      alert(data.message);
      setRecentSales(prev => [productName, ...prev.slice(0, 5)]); // Store last 5 recent sales
      fetchProducts();
      fetchLowStock();
    } catch (error) {
      console.error("Error registering sale:", error);
    }
  };

  return (
    <Box
      component={motion.div}
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      sx={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        p: 3,
        bgcolor: "#f5f5f5"
      }}
    >
      <Card sx={{ width: "80%", p: 2, borderRadius: 2 }}>
        <CardContent>
          <Typography variant="h5" sx={{ mb: 2 }}>Retail Shop Sales</Typography>

          {/* Search Bar */}
          <TextField
            label="Search Product"
            variant="outlined"
            fullWidth
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />

          {/* Product Suggestions (Displayed in a row like an array list) */}
          {search && (
            <Box
              sx={{
                display: "flex",
                overflowX: "auto",
                gap: 1,
                mt: 2,
                p: 1,
                bgcolor: "white",
                borderRadius: 2,
                boxShadow: 2
              }}
            >
              {products
                .filter(p => p.name.toLowerCase().includes(search.toLowerCase()))
                .map((p, index) => (
                  <Box
                    key={index}
                    sx={{
                      bgcolor: colors[index % colors.length],
                      color: "white",
                      px: 2,
                      py: 1,
                      borderRadius: 2,
                      whiteSpace: "nowrap",
                      cursor: "pointer",
                      "&:hover": { opacity: 0.8 }
                    }}
                    onClick={() => handleSale(p.name)}
                  >
                    {p.name} - {p.quantity} left
                  </Box>
                ))}
            </Box>
          )}

          {/* Frequent & Recent Sales (Also in a horizontal list) */}
          <Box
            sx={{
              display: "flex",
              overflowX: "auto",
              gap: 1,
              mt: 2,
              p: 1,
              bgcolor: "#fff",
              borderRadius: 2,
              boxShadow: 2
            }}
          >
            <Typography variant="subtitle1" sx={{ fontWeight: "bold", mr: 1 }}>Recent Sales:</Typography>
            {recentSales.map((p, index) => (
              <Box
                key={index}
                sx={{
                  bgcolor: colors[index % colors.length],
                  color: "white",
                  px: 2,
                  py: 1,
                  borderRadius: 2,
                  whiteSpace: "nowrap"
                }}
              >
                {p}
              </Box>
            ))}
          </Box>

          {/* Low Stock Alerts */}
          {lowStock.length > 0 && (
            <Box
              sx={{
                display: "flex",
                overflowX: "auto",
                gap: 1,
                mt: 2,
                p: 1,
                bgcolor: "#ffcccc",
                borderRadius: 2,
                boxShadow: 2
              }}
            >
              <Typography variant="subtitle1" sx={{ fontWeight: "bold", mr: 1 }}>⚠️ Low Stock:</Typography>
              {lowStock.map((p, index) => (
                <Box
                  key={index}
                  sx={{
                    bgcolor: "#ff3333",
                    color: "white",
                    px: 2,
                    py: 1,
                    borderRadius: 2,
                    whiteSpace: "nowrap"
                  }}
                >
                  {p.name} - {p.current_stock} left
                </Box>
              ))}
            </Box>
          )}

          {/* Register Sale Button */}
          <Button variant="contained" color="primary" fullWidth sx={{ mt: 2 }} onClick={() => alert("Feature in progress")}>
            Register Sale
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default SalesRegister;
